// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/metatx/ERC2771Context.sol";

/**
 @notice This contract manages the role-based access control via _checkRole() with meaningful 
 error messages if the user does not have the requested role. This contract is inherited by 
 PolicyManager, RuleRegistry, KeyringCredentials, IdentityTree, WalletCheck and 
 KeyringZkCredentialUpdater.
 */

abstract contract KeyringAccessControl is ERC2771Context, AccessControl {

    address private constant NULL_ADDRESS = address(0);

    // Reservations hold space in upgradeable contracts for future versions of this module.
    bytes32[50] private _reservedSlots;

    error Unacceptable(string reason);

    error Unauthorized(
        address sender,
        string module,
        string method,
        bytes32 role,
        string reason,
        string context
    );

    /**
     * @param trustedForwarder Contract address that is allowed to relay message signers.
     */
    constructor(address trustedForwarder) ERC2771Context(trustedForwarder) {
        if (trustedForwarder == NULL_ADDRESS)
            revert Unacceptable({
                reason: "trustedForwarder cannot be empty"
            });
    }

    /**
     * @notice Disables incomplete ERC165 support inherited from oz/AccessControl.sol
     * @return bool Never returned.
     * @dev Always reverts. Do not rely on ERC165 support to interact with this contract.
     */
    function supportsInterface(bytes4 /*interfaceId */) public view virtual override returns (bool) {
        revert Unacceptable ({ reason: "ERC2165 is unsupported" });
    }

    /**
     * @notice Role-based access control.
     * @dev Reverts if the account is missing the role.
     * @param role The role to check. 
     * @param account An address to check for the role.
     * @param context For reporting purposes. Usually the function that requested the permission check.
     */
    function _checkRole(
        bytes32 role,
        address account,
        string memory context
    ) internal view {
        if (!hasRole(role, account))
            revert Unauthorized({
                sender: account,
                module: "KeyringAccessControl",
                method: "_checkRole",
                role: role,
                reason: "sender does not have the required role",
                context: context
            });
    }

    /**
     * @notice Returns ERC2771 signer if msg.sender is a trusted forwarder, otherwise returns msg.sender.
     * @return sender User deemed to have signed the transaction.
     */
    function _msgSender()
        internal
        view
        virtual
        override(Context, ERC2771Context)
        returns (address sender)
    {
        return ERC2771Context._msgSender();
    }

    /**
     * @notice Returns msg.data if not from a trusted forwarder, or truncated msg.data if the signer was 
     appended to msg.data
     * @dev Although not currently used, this function forms part of ERC2771 so is included for completeness.
     * @return data Data deemed to be the msg.data
     */
    function _msgData()
        internal
        view
        virtual
        override(Context, ERC2771Context)
        returns (bytes calldata)
    {
        return ERC2771Context._msgData();
    }
}

contracts/keyring/consent/Consent.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "../interfaces/IConsent.sol";
import "../access/KeyringAccessControl.sol";

contract Consent is IConsent, KeyringAccessControl {

    uint256 private constant MINIMUM_MAX_CONSENT_PERIOD = 1 hours;
    uint256 public immutable override maximumConsentPeriod;

    /**
     * @dev Mapping of Traders to their associated consent deadlines.
     */
    mapping(address => uint256) public override userConsentDeadlines;

    /**
     * @param trustedForwarder The address of a trustedForwarder contract.
     * @param maximumConsentPeriod_ The upper limit for user consent deadlines. 
     */
    constructor(
        address trustedForwarder, 
        uint256 maximumConsentPeriod_
    ) 
        KeyringAccessControl(trustedForwarder)
    {
        if (maximumConsentPeriod_ < MINIMUM_MAX_CONSENT_PERIOD)
            revert Unacceptable({
                reason: "The maximum consent period must be at least 1 hour"
            });

        maximumConsentPeriod = maximumConsentPeriod_;
    }

    /**
     * @notice A user may grant consent to service mitigation measures. 
     * @dev The deadline must be no further in the future than the maximumConsentDeadline.
     * @param revocationDeadline The consent will automatically expire at the deadline. 
     */
    function grantDegradedServiceConsent(uint256 revocationDeadline) external override {
        if(revocationDeadline < block.timestamp)
            revert Unacceptable({
                reason: "revocation deadline cannot be in the past"
            });
        if(revocationDeadline > block.timestamp + maximumConsentPeriod)
            revert Unacceptable({
                reason: "revocation deadline is too far in the future"
            });
        userConsentDeadlines[_msgSender()] = revocationDeadline;
        emit GrantDegradedServiceConsent(_msgSender(), revocationDeadline);
    }

    /**
     * @notice A user may revoke their consent to mitigation measures. 
     */
    function revokeMitigationConsent() external override {
        userConsentDeadlines[_msgSender()] = 0;
        emit RevokeDegradedServiceConsent(_msgSender());
    }

    /**
     * @param user The user to inspect. 
     * @return doesIndeed True if the user's consent deadline is in the future.
     */
    function userConsentsToMitigation(address user) public view override returns (bool doesIndeed) {
        doesIndeed = userConsentDeadlines[user] >= block.timestamp;
    }

}

contracts/keyring/credentialUpdater/Keyr.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "../interfaces/IKeyringZkCredentialUpdater.sol";
import "../interfaces/IPolicyManager.sol";
import "../interfaces/IKeyringCredentials.sol";
import "../interfaces/IRuleRegistry.sol";
import "../interfaces/IWalletCheck.sol";
import "../interfaces/IKeyringZkVerifier.sol";
import "../lib/Pack12x20.sol";
import "../access/KeyringAccessControl.sol";

/**
 @notice This contract acts as a credentials cache updater. It needs the ROLE_CREDENTIAL_UPDATER 
 permission in the KeyringCredentials contract in order to record credentials. The contract checks 
 client-generated zero-knowledge proofs of attestations about admission policy eligibility and 
 therefore enforces the protocol.
 */

contract KeyringZkCredentialUpdater is
    IKeyringZkCredentialUpdater,
    KeyringAccessControl
{
    using Pack12x20 for uint32[12];
    using Pack12x20 for uint256;

    address private constant NULL_ADDRESS = address(0);
    bytes32 private constant NULL_BYTES32 = bytes32(0);
    IRuleRegistry private immutable RULE_REGISTRY;
    address public immutable override POLICY_MANAGER;
    address public immutable override KEYRING_CREDENTIALS;
    address public immutable override KEYRING_ZK_VERIFIER;

    /**
     * @param trustedForwarder Contract address that is allowed to relay message signers.
     * @param keyringCredentials The address for the deployed KeyringCredentials contract to write to.
     * @param policyManager The address for the deployed PolicyManager contract to read from.
     * @param keyringZkVerifier On-chain instance of the stateless Keyring ZK verifier contract.
     */
    constructor(
        address trustedForwarder,
        address keyringCredentials,
        address policyManager,
        address keyringZkVerifier
    ) KeyringAccessControl(trustedForwarder) {
        if (keyringCredentials == NULL_ADDRESS)
            revert Unacceptable({
                reason: "keyringCredentials cannot be empty"
            });
        if (policyManager == NULL_ADDRESS)
            revert Unacceptable({
                reason: "policyManager cannot be empty"
            });
        if (keyringZkVerifier == NULL_ADDRESS)
            revert Unacceptable({
                reason: "keyringZkVerifier cannot be empty"
            });
        RULE_REGISTRY = IRuleRegistry(IPolicyManager(policyManager).ruleRegistry());
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());

        POLICY_MANAGER = policyManager;
        KEYRING_CREDENTIALS = keyringCredentials;
        KEYRING_ZK_VERIFIER = keyringZkVerifier;

        emit CredentialUpdaterDeployed(
            _msgSender(),
            trustedForwarder,
            keyringCredentials,
            policyManager,
            keyringZkVerifier
        );
    }

    /**
     * @notice Updates the credential cache if the request is acceptable.
     * @dev The attestor must be valid for all policy disclosures. For this to be possible, it must have been admitted
     to the system globally before it was selected for a policy. The two zero-knowledge proof share parameters that 
     ensure that both proofs were derived from the same identity commitment. If the root age used to construct proofs
     if older than the policy time to live (ttl), the root will be considered acceptable with an age of zero, provided
     that the number of root successors is less than or equal to the policy acceptRoots (accept most recent n roots).
     * @param attestor The identityTree contract with a root that contains the user's identity commitment. Must be 
     present in the current attestor list for all policy disclosures in the authorization proof. 
     * @param membershipProof A zero-knowledge proof of identity commitment membership in the identity tree. Contains an
     external nullifier and nullifier hash that must match the parameters of the authorization proof. 
     * @param authorizationProof A zero-knowledge proof of compliance with up to 24 policy disclosures. Contains an
     external nullifier and nullifier hash that must match the parameters of the membershiip proof. 
     */
    function updateCredentials(
        address attestor,
        IKeyringZkVerifier.IdentityMembershipProof calldata membershipProof,
        IKeyringZkVerifier.IdentityAuthorisationProof calldata authorizationProof
    ) external override {

        bytes32 requireBackdoor;

        if (!IPolicyManager(POLICY_MANAGER).isGlobalAttestor(attestor))
            revert Unacceptable({ reason: "attestor unacceptable" });

        uint32 policyId;
        address sender = _msgSender();
        address trader = address(uint160(authorizationProof.tradingAddress));
        if (sender != trader)
            revert Unacceptable ({ reason: "only trader can update trader credentials" });
        bytes32 root = bytes32(membershipProof.root);

        uint32[12] memory policyList0 = unpack12x20(authorizationProof.policyDisclosures[0]);
        uint32[12] memory policyList1 = unpack12x20(authorizationProof.policyDisclosures[1]);

        if(!IKeyringZkVerifier(KEYRING_ZK_VERIFIER).checkClaim(
            membershipProof,
            authorizationProof
        )) revert Unacceptable({
            reason: "Proof unacceptable"});

        uint256 rootTime = IDegradable(attestor).subjectUpdates(root);

        for(uint256 i = 0; i < 24; i++) {

            policyId = (i / 12 == 0) ? policyList0[i % 12] : policyList1[i % 12];
            if(policyId == 0) break;

            if(!checkPolicy(policyId, attestor))
                revert Unacceptable({
                    reason: "policy or attestor unacceptable"
                });

            /**
             @dev Set the credential cache. 
             */
            IKeyringCredentials(KEYRING_CREDENTIALS).setCredential(
                trader, 
                policyId,                
                rootTime);

            /**
             @dev This verifier supports exactly one backdoor per proof. Therefore, all policies in the
             disclosure must require the same backdoor, or no backdoor. 
             */
            uint256 policyBackdoorCount = IPolicyManager(POLICY_MANAGER).policyBackdoorCount(policyId);
            if (policyBackdoorCount > 1) revert Unacceptable({ reason: "multiple policy backdoors are not supported" });
            if (policyBackdoorCount == 1) {
                if (requireBackdoor == NULL_BYTES32) {
                    requireBackdoor = IPolicyManager(POLICY_MANAGER).policyBackdoorAtIndex(policyId, 0);
                } else {
                    if(requireBackdoor != IPolicyManager(POLICY_MANAGER).policyBackdoorAtIndex(policyId,0))
                        revert Unacceptable({
                            reason: "all policies in the proof must rely on the same backdoor or no backdoor"
                        });
                }
            }
        }

        /**
         @dev If any policy requires a backdoor, confirm the backdoor is contained in the Proof. 
         */
        if (requireBackdoor != NULL_BYTES32) {
            uint256[2] memory requirePubKey = IPolicyManager(POLICY_MANAGER).backdoorPubKey(requireBackdoor);
            if (requirePubKey[0] != authorizationProof.regimeKey[0] ||
                requirePubKey[1] != authorizationProof.regimeKey[1]) 
            {
                revert Unacceptable ({
                    reason: "Proof does not contain required backdoor regimeKey"
                });
            }
        }

        emit AcceptCredentialUpdate(
            sender, 
            trader, 
            membershipProof, 
            authorizationProof, 
            rootTime);
    }

  /**
     * @notice The identity tree must be a policy attestor and the policy rule cannot be toxic.
     * @dev Use static call to inspect response.
     * @param policyId The policy to inspect.
     * @param attestor The identity tree contract address to compare to the policy attestors.
     * @return acceptable True if the policy rule is not toxic and the identity tree is authoritative for the policy.
     */    
    function checkPolicy(
        uint32 policyId, 
        address attestor
    ) public override returns (bool acceptable)
    {
        IPolicyManager p = IPolicyManager(POLICY_MANAGER);
        if(!p.isPolicy(policyId)) return false;

        PolicyStorage.PolicyScalar memory policyScalar = 
            IPolicyManager(POLICY_MANAGER).policyScalarActive(policyId);

        acceptable = !(RULE_REGISTRY.ruleIsToxic(policyScalar.ruleId)) &&
            p.isPolicyAttestor(policyId, attestor);
    }

    /**
     * @notice Packs uint32[12] into uint256 with 20-bit precision.
     * @dev uint32 Inputs are limited to 20 bits of magnitude.
     * @param input 20 bit unsigned integers cast as uint32.
     * @return packed Uint256 packed format contained encoding of 12 20-bit uints. 
     */
    function pack12x20(uint32[12] calldata input) public pure override returns (uint256 packed) {
        packed = input.pack();
    }

    /**
     * @notice Unpacks packed elements as 20-bit uint32[12].
     * @param packed Packed format, 12 x 20-bit unsigned integers, tightly packed. 
     * @return unpacked Uint32[12], 20-bit precision.
     */
    function unpack12x20(uint256 packed) public pure override returns (uint32[12] memory unpacked) {
        unpacked = packed.unpack();
    }
}

contracts/keyring/degradable/Degradable..content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "../interfaces/IDegradable.sol";
import "../interfaces/IPolicyManager.sol";
import "../consent/Consent.sol";

/**
 * @title Degradable
 * @dev A contract that allows services to specify how to mitigate service interuptions
 * using policy-specific parameters.
 */

contract Degradable is IDegradable, Consent {
    uint256 private constant FIRST_CONFIGURABLE_POLICY = 1;
    uint256 private constant MAX_DEGRATION_PERIOD = 60 days;
    uint256 private constant MAX_DEGRATION_FRESHNESS_PERIOD = 365 days * 50;
    uint256 private constant MAX_CONSENT_DEADLINE = 10 days;
    address private constant NULL_ADDRESS = address(0);

    bytes32 public constant override ROLE_SERVICE_SUPERVISOR = keccak256(abi.encodePacked("supervisor"));
    uint256 public constant override defaultDegradationPeriod = 7 days;
    uint256 public constant override defaultFreshnessPeriod = 30 days;
    address public immutable override policyManager;
    uint256 public override lastUpdate;

    /**
     * @dev Mapping of storage subjects to their associated update timestamps.
     */
    mapping(bytes32 => uint256) public override subjectUpdates;

    /**
     * @dev Mapping of policy IDs to their associated mitigation parameters.
     */
    mapping(uint32 => MitigationParameters) private _mitigationParameters;

    /**
     * @dev Modifier that checks if the caller has the policy admin or supervisor role.
     * @param policyId The ID of the policy.
     */
    modifier onlyPolicyAdminOrSupervisor(uint32 policyId) {
        if (!hasRole(ROLE_SERVICE_SUPERVISOR, _msgSender())) {
            bytes32 role = bytes32(uint256(uint32(policyId)));
            if (!IPolicyManager(policyManager).hasRole(role, _msgSender())) {
                revert Unauthorized({
                    sender: _msgSender(),
                    module: "Degradable",
                    method: "_checkRole",
                    role: role,
                    reason: "sender does not have the required role",
                    context: "Degradable:onlyPolicyAdminOrSupervisor"
                });
            }
        }
        _;
    }

    /**
     * @param trustedForwarder Address of the trusted forwarder contract.
     * @param policyManager_ Address of the policy manager contract.
     * @param maximumConsentPeriod_ Maximum consent duration a user will be allowed to grant.
     */
    constructor(
        address trustedForwarder,
        address policyManager_,
        uint256 maximumConsentPeriod_
    ) Consent(trustedForwarder, maximumConsentPeriod_) {
        if (trustedForwarder == NULL_ADDRESS) revert Unacceptable({ reason: "trustedForwarder cannot be empty" });
        if (policyManager_ == NULL_ADDRESS) revert Unacceptable({ reason: "policyManager_ cannot be empty" });
        policyManager = policyManager_;
    }

    /**
     * @notice Record the timestamp of the last update to the contract.
     * @dev Must be called by derived contracts.
     * @param subject The subject to update.
     * @param time The time to record.
     */
    function _recordUpdate(address subject, uint256 time) internal {
        _recordUpdate(bytes32(uint256(uint160(subject))), time);
    }

    /**
     * @notice Record the timestamp of the last update to the contract.
     * @dev Must be called by derived contracts.
     * @param subject The subject to update.
     * @param time The time to record.
     */
    function _recordUpdate(bytes32 subject, uint256 time) internal {
        if (time > block.timestamp) revert Unacceptable({ reason: "time must be in the past" });
        if (subjectUpdates[subject] > time) revert Unacceptable({ reason: "time is older than existing update" });
        if (time > lastUpdate) lastUpdate = time;
        subjectUpdates[subject] = time;
    }

    /**
     * @dev Set the mitigation parameters for a policy.
     * @param policyId The ID of the policy.
     * @param degradationPeriod_ The time period after which the service is considered degraded.
     * @param degradationFreshness_ Used by derived service contracts to include or exclude data that was recorded
     * before the service fell into the degraded state.
     */
    function setPolicyParameters(
        uint32 policyId,
        uint256 degradationPeriod_,
        uint256 degradationFreshness_
    ) external override onlyPolicyAdminOrSupervisor(policyId) {
        if (policyId < FIRST_CONFIGURABLE_POLICY)
            revert Unacceptable({ reason: "Cannot configure genesis policies 0 and 1" });
        // acceptable range is 0 to very large
        if (degradationPeriod_ > MAX_DEGRATION_PERIOD)
            revert Unacceptable({ reason: "degradationPeriod cannot exceed 60 days" });
        if (degradationFreshness_ > MAX_DEGRATION_FRESHNESS_PERIOD)
            revert Unacceptable({ reason: "degradationFreshness cannot exceed 50 years" });
        _mitigationParameters[policyId] = MitigationParameters({
            degradationPeriod: degradationPeriod_,
            degradationFreshness: degradationFreshness_
        });
        emit SetPolicyParameters(_msgSender(), policyId, degradationPeriod_, degradationFreshness_);
    }

    /**
     * @notice Check the subjects's last recorded update and compare to policy ttl, with mitigation.
     * @dev Fallback to mitigation measures if acceptable. Use staticCall to inspect.
     * @param observer The user who must consent to reliance on degraded services.
     * @param subject The subject to inspect.
     * @param policyId PolicyId to consider for possible mitigation.
     */
    function _checkKey(address observer, address subject, uint32 policyId) internal returns (bool pass) {
        pass = _checkKey(observer, bytes32(uint256(uint160(subject))), policyId);
    }

    /**
     * @notice Check the subject's last recorded update and compare to policy ttl, with mitigation.
     * @dev Fallback to mitigation measures if acceptable. Use staticCall to inspect.
     * @param observer The user who must consent to reliance on degraded services.
     * @param subject The subject to inspect.
     * @param policyId PolicyId to consider for possible mitigation.
     */
    function _checkKey(address observer, bytes32 subject, uint32 policyId) internal returns (bool pass) {
        uint256 time = block.timestamp;
        uint256 lastSubjectUpdate = subjectUpdates[subject];

        // normal operations
        uint256 ttl = IPolicyManager(policyManager).policyTtl(policyId);
        if (time <= lastSubjectUpdate + ttl) return true;

        // check for mitigation
        pass = _canMitigate(observer, policyId, time, lastSubjectUpdate);
    }

    /**
     * @notice A Degradable service implments a compromised process.
     * @dev Must consult user Consent and Policy parameters. Must return false unless degraded.
     * Use staticCall to inspect.
     * @param observer The user who must consent to reliance on degraded services.
     * @param subject The topic to inspect.
     * @param policyId The policyId for mitigation parameters.
     */
    function canMitigate(
        address observer,
        bytes32 subject,
        uint32 policyId
    ) public view virtual override returns (bool canIndeed) {
        canIndeed = _canMitigate(observer, policyId, block.timestamp, subjectUpdates[subject]);
    }

    /**
     * @notice A Degradable service implments a compromised process.
     * @dev Must consult user Consent and Policy parameters. Must return false unless degraded.
     * @param observer The user who must consent to reliance on degraded services.
     * @param policyId The policyId for mitigation parameters.
     * @param time Derived contracts and callers provide current blocktime for comparison.
     * @param subjectUpdated Derived contracts and callers provide last subject update.
     */
    function _canMitigate(
        address observer,
        uint32 policyId,
        uint256 time,
        uint256 subjectUpdated
    ) internal view returns (bool canIndeed) {
        if (!_isDegraded(policyId, time)) return false;
        if (!_isMitigationQualified(subjectUpdated, policyId)) return false;
        if (userConsentDeadlines[observer] < time) return false;
        canIndeed = true;
    }

    /**
     * @notice A service is degraded if there has been no update for longer than the degradation period.
     * @param policyId The policyId to inspect.
     * @return isIndeed True if the service is considered degraded by the Policy.
     */
    function isDegraded(uint32 policyId) public view override returns (bool isIndeed) {
        isIndeed = _isDegraded(policyId, block.timestamp);
    }

    /**
     * @notice A service is degraded if there has been no update for longer than the degradation period.
     * @param policyId The policyId to inspect.
     * @param time Time to compare.
     * @return isIndeed True if the service is considered degraded by the Policy.
     */
    function _isDegraded(uint32 policyId, uint256 time) internal view returns (bool isIndeed) {
        if (lastUpdate == 0) return false;
        uint256 policyDegradationPeriod = _mitigationParameters[policyId].degradationPeriod;
        isIndeed = (policyDegradationPeriod == 0)
            ? time > lastUpdate + defaultDegradationPeriod
            : time > lastUpdate + policyDegradationPeriod;
    }

    /**
     * @notice Evaluate if existing services records can be used for mitigation measures.
     * @param subject Key to inspect.
     * @param policyId Policy to inspect for mitigation parameters.
     * @return qualifies True if the birthday is after the cutoff deadline for the service set by the Policy admin.
     */
    function isMitigationQualified(bytes32 subject, uint32 policyId) public view override returns (bool qualifies) {
        qualifies = _isMitigationQualified(subjectUpdates[subject], policyId);
    }

    /**
     * @notice Evaluate if existing services records can be used for mitigation measures.
     * @param lastSubjectUpdate Last recorded update for the subject.
     * @param policyId Policy to inspect for mitigation parameters.
     * @return qualifies True if the subject update time is after the mitigation cutoff  for the
     * service set by the Policy admin.
     */
    function _isMitigationQualified(uint256 lastSubjectUpdate, uint32 policyId) internal view returns (bool qualifies) {
        qualifies = lastSubjectUpdate >= mitigationCutoff(policyId);
    }

    /**
     * @notice The degradation period is maximum interval between updates before the policy considers the
     * service degraded.
     * @param policyId The policyId to inspect.
     * @return inSeconds The degradation period for the policy.
     */
    function degradationPeriod(uint32 policyId) public view override returns (uint256 inSeconds) {
        if(!IPolicyManager(policyManager).isPolicy(policyId)) revert Unacceptable({ reason: "unknown policy" });
        uint256 policyDegradationPeriod = _mitigationParameters[policyId].degradationPeriod;
        inSeconds = (policyDegradationPeriod == 0) ? defaultDegradationPeriod : policyDegradationPeriod;
    }

    /**
     * @notice A service may implement a mitigation strategy to employ while the service is degraded.
     * @dev Service mitigations can use this parameter.
     * @param policyId The policyId to inspect.
     * @return inSeconds The freshness period for the policy.
     */
    function degradationFreshness(uint32 policyId) public view override returns (uint256 inSeconds) {
        if(!IPolicyManager(policyManager).isPolicy(policyId)) revert Unacceptable({ reason: "unknown policy" });
        uint256 policyDegradationFreshness = _mitigationParameters[policyId].degradationFreshness;
        inSeconds = (policyDegradationFreshness == 0) ? defaultFreshnessPeriod : policyDegradationFreshness;
    }

    /**
     * @notice Service degradation mitigation measures depend on the oldest acceptable update.
     * @param policyId The policyId to consult for a cutoff time.
     * @return cutoffTime The oldest update that will be useable for mitigation measures.
     */
    function mitigationCutoff(uint32 policyId) public view override returns (uint256 cutoffTime) {
        uint256 _degradationFreshness = degradationFreshness(policyId);
        if (lastUpdate == 0) return block.timestamp;
        if (_degradationFreshness > lastUpdate) {
            return 0;
        } else {
            return lastUpdate - _degradationFreshness;
        }
    }
}

contracts/keyring/exemptions/ExemptionsM.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "../interfaces/IExemptionsManager.sol";
import "../interfaces/IPolicyManager.sol";
import "../access/KeyringAccessControl.sol";
import "../lib/AddressSet.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";

/**
 * @title ExemptionsManager
 * @notice This contract manages the exemptions for the policy engine.
 * It allows an exemptions admin to manage global exemptions and policy admins
 * to manage policy-specific exemptions.
 * @dev Inherits from IExemptionsManager, KeyringAccessControl, and Initializable.
 */

contract ExemptionsManager is IExemptionsManager, KeyringAccessControl, Initializable {
    using AddressSet for AddressSet.Set;

    bytes32 public constant override ROLE_GLOBAL_EXEMPTIONS_ADMIN = keccak256("x");
    address private constant NULL_ADDRESS = address(0);
    AddressSet.Set private globalExemptionsSet;
    mapping(uint32 => AddressSet.Set) private policyExemptions;

    address public override policyManager;
    mapping(address => string) public override exemptionDescriptions;

    /**
     * @notice Keyring Governance has exclusive access to global exemptions.
     * @dev Reverts if the user doesn't have the global validation admin role.
     */
    modifier onlyExemptionsAdmin() {
        _checkRole(ROLE_GLOBAL_EXEMPTIONS_ADMIN, _msgSender(), "em:ea");
        _;
    }

    /**
     * @notice Only the Policy Admin can manipulate policy-specific settings.
     * @param policyId The policyId to check.
     * @dev Reverts if the sender is not an admin for the specified policy.
     */
    modifier onlyPolicyAdmin (uint32 policyId) {
        bytes32 role = bytes32(uint256(policyId));
        if (!IPolicyManager(policyManager).hasRole(role, _msgSender()))
            revert Unauthorized({
                sender: _msgSender(),
                module: "KeyringAccessControl",
                method: "_checkRole",
                role: role,
                reason: "sender does not have the required role",
                context: "ExemptionsManager:onlyPolicyAdmin"
            });
        _;
    }

    constructor(address trustedForwarder) KeyringAccessControl(trustedForwarder){}

    /**
     * @notice Initializes the contract with the provided policyManager address.
     * @dev Can only be called once, as it is an initializer function.
     * @param policyManager_ The address of the PolicyManager contract.
     */
    function init(address policyManager_) external override initializer {
        if(policyManager_ == NULL_ADDRESS) revert Unacceptable({ reason: "policyManager_ cannot be empty" });
        policyManager = policyManager_;
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        emit ExemptionsManagerInitialized(_msgSender(), policyManager_);
    }

    /**
     * @notice Admits the specified addresses as global exemptions with a description.
     * @dev Can only be called by the exemptions admin. Admission is irrevocable. 
     * @param exemptAddresses An array of addresses to be admitted as global exemptions.
     * @param description A human-readable description of the exempt addresses.
     */
    function admitGlobalExemption(
        address[] calldata exemptAddresses,
        string memory description
    ) 
        external
        override
        onlyExemptionsAdmin
    {
        for (uint256 i=0; i<exemptAddresses.length; i++) {
            globalExemptionsSet.insert(exemptAddresses[i], "PolicyStorage:insertGlobalExemptAddress");
            _updateGlobalExemption(exemptAddresses[i], description);
            emit AdmitGlobalExemption(_msgSender(), exemptAddresses[i], description);
        }
    }

    /**
     * @notice Updates the description of an existing global exemption address.
     * @dev Can only be called by the exemptions admin.
     * @param exemptAddress The exempt address whose description needs to be updated.
     * @param description The new human-readable description for the exempt address.
     */
    function updateGlobalExemption(
        address exemptAddress,
        string memory description
    ) 
        external
        override
        onlyExemptionsAdmin
    {
        // null address is allowed to be except
        _updateGlobalExemption(exemptAddress, description);  
        emit UpdateGlobalExemption(_msgSender(), exemptAddress, description);
    }

    function _updateGlobalExemption(
        address exemptAddress,
        string memory description
    )
        internal
    {
        // null address is allowed to be exempt
        if (!globalExemptionsSet.exists(exemptAddress))
            revert Unacceptable({
                reason: "unknown exemptAddress"
            });
        if (bytes(description).length == 0)
            revert Unacceptable({
                reason: "description cannot be empty"
            });
        exemptionDescriptions[exemptAddress] = description;  
    }

    /**
     * @notice Approves the specified exemptions for a given policy.
     * @dev Can only be called by a policy admin for the specified policyId. Only policies
     * that are admitted globally are eligable for approval. Approval is irrevocable. 
     * @param policyId The ID of the policy for which exemptions are being approved.
     * @param exemptions An array of addresses to be approved as exemptions for the policy.
     */
    function approvePolicyExemptions(
        uint32 policyId,
        address[] memory exemptions
    )
        external 
        override
        onlyPolicyAdmin(policyId)
    {
        for(uint256 i=0; i<exemptions.length; i++) {
            if(!isGlobalExemption(exemptions[i]))
                revert Unacceptable ({
                    reason: "exemption is not approved"
                });
            policyExemptions[policyId].insert(
                exemptions[i],
                "ExemptionsManager.approvePolicyExemptions"
            );
            emit ApprovePolicyExemptions(_msgSender(), policyId, exemptions[i]);
        }
    }

    /**
     * @notice Returns the count of global exemptions.
     * @return count The count of global exemptions.
     */
    function globalExemptionsCount() external view override returns (uint256 count) {
        count = globalExemptionsSet.count();
    }

    /**
     * @notice Returns the global exemption address at the specified index.
     * @param index The index of the exemption in the global exemptions list.
     * @return exemption The global exemption address at the specified index.
     */
    function globalExemptionAtIndex(uint256 index) external view override returns (address exemption) {
        if (index >= globalExemptionsSet.count())
            revert Unacceptable ({
                reason: "index out of range"
            });
        exemption = globalExemptionsSet.keyAtIndex(index);
    }

    /**
     * @notice Checks if a given address is a global exemption.
     * @param exemption The address to be checked as a global exemption.
     * @return isIndeed True if the address is a global exemption, otherwise false.
     */
    function isGlobalExemption(address exemption) public view override returns (bool isIndeed) {
        isIndeed = globalExemptionsSet.exists(exemption);
    }

    /**
     * @notice Returns the count of policy-specific exemptions for a given policyId.
     * @param policyId The ID of the policy for which exemptions count is required.
     * @return count The count of exemptions for the given policyId.
     */
    function policyExemptionsCount(uint32 policyId) external view override returns (uint256 count) {
        count = policyExemptions[policyId].count();
    }

    /**
     * @notice Returns the policy-specific exemption address at the specified index for a given policyId.
     * @param policyId The ID of the policy for which the exemption is required.
     * @param index The index of the exemption in the policy exemptions list.
     * @return exemption The exemption address at the specified index.
     */
    function policyExemptionAtIndex(uint32 policyId, uint256 index) external view override returns (address exemption) {
        AddressSet.Set storage pe = policyExemptions[policyId];
        if (index >= pe.count())
            revert Unacceptable({
                reason: "index out of range"
            });
        exemption = pe.keyAtIndex(index);
    }

    /**
     * @notice Checks if a given address is an exemption for a specified policyId.
     * @param policyId The ID of the policy for which the exemption check is required.
     * @param exemption The address to be checked as an exemption for the policy.
     * @return isIndeed True if the address is an exemption for the policy, otherwise false.
     */
    function isPolicyExemption(uint32 policyId, address exemption) external view override returns (bool isIndeed) {
        isIndeed = policyExemptions[policyId].exists(exemption);
    }    

}

contracts/keyring/forwarder/NoImplementa.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

/**
 * @notice This stub provides a hint for hardhat artifacts and typings. It is a non-functional
 implementation to deploy behind a TransparentUpgradeableProxy. The proxy address will be passed
 to constructors that expect an immutable trusted forwarder for future gasless transaction
 support (trustedForwarder). This contract implements the essential functions as stubs that
 fail harmlessly. 
 */

contract NoImplementation {

    struct ForwardRequest {
        address from;
        address to;
        uint256 value;
        uint256 gas;
        uint256 nonce;
        bytes data;
    }

    error NotImplemented(address sender, string message);

    function getNonce(address /* from */) public pure returns (uint256) {
        return 0;
    }

    function verify(ForwardRequest calldata /* req */, bytes calldata /* signature */) public pure returns (bool) {
        return false;
    }

    function execute(ForwardRequest calldata /* req */, bytes calldata /* signature */)
        public
        payable
        returns (bool, bytes memory)
    {
        revert NotImplemented({
            sender: msg.sender,
            message: "This forwarder is not operational"
        });
    }
}

contracts/keyring/identityTree/IdentityT.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "../interfaces/IIdentityTree.sol";
import "../degradable/Degradable.sol";
import "../lib/Bytes32Set.sol";

/**
 @notice This contract holds the history of identity tree merkle roots announced by the aggregator. 
 Each root has an associated birthday that records when it was created. Zero-knowledge proofs rely
 on these roots. Claims supported by proofs are considered to be of the same age as the roots they
 rely on for validity. 
 */

contract IdentityTree is IIdentityTree, Degradable { 

    using Bytes32Set for Bytes32Set.Set;

    uint256 private constant INFINITY = ~uint256(0);
    address private constant NULL_ADDRESS = address(0);
    bytes32 private constant NULL_BYTES32 = bytes32(0);
    bytes32 public constant override ROLE_AGGREGATOR = keccak256("aggregator role");

    Bytes32Set.Set merkleRootSet;

    modifier onlyAggregator() {
        _checkRole(ROLE_AGGREGATOR, _msgSender(), "IdentityTree::onlyAggregator");
        _;
    }

    /**
     * @param trustedForwarder_ Contract address that is allowed to relay message signers.
     * @param policyManager_ The policy manager contract address.
     * @param maximumConsentPeriod_ The maximum allowable user consent period.
     */
    constructor(
        address trustedForwarder_,
        address policyManager_,
        uint256 maximumConsentPeriod_
    ) 
        Degradable(
            trustedForwarder_,
            policyManager_,
            maximumConsentPeriod_
        ) 
    {
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        emit Deployed(_msgSender(), trustedForwarder_, policyManager_, maximumConsentPeriod);
    }

    /**
     * @notice The aggregator can set roots with non-zero birthdays.
     * @dev Explicit birthday declaration ensures that root age is not extended by mining delays. 
     * @param merkleRoot The merkleRoot to set.
     * @param birthday The timestamp of the merkleRoot. 0 to invalidate the root.
     */
    function setMerkleRootBirthday(bytes32 merkleRoot, uint256 birthday) external override onlyAggregator {
        if (birthday > block.timestamp)
            revert Unacceptable({
                reason: "birthday cannot be in the future"
            });
        if (merkleRoot == NULL_BYTES32)
            revert Unacceptable({
                reason: "merkle root cannot be empty"
            });
        if (birthday < lastUpdate) 
            revert Unacceptable({
                reason: "birthday precedes previously recorded birthday"
            });
        _recordUpdate(merkleRoot, birthday);
        merkleRootSet.insert(merkleRoot, "IdentityTree::setMerkleRoot");
        emit SetMerkleRootBirthday(merkleRoot, birthday);
    }

    /**
     * @notice Inspect the Identity Tree
     * @dev Use static calls to inspect.
     * @param observer The observer for degradation mitigation consent. 
     * @param merkleRoot The merkle root to inspect. 
     * @param admissionPolicyId The admission policy for the credential to inspect.
     * @return passed True if a valid merkle root exists or if mitigation measures are applicable.
     */
    function checkRoot(
        address observer, 
        bytes32 merkleRoot,
        uint32 admissionPolicyId
    ) external override returns (bool passed) {

        passed = _checkKey(
            observer,
            merkleRoot,
            admissionPolicyId
        );
    }

    /**
     * @return count The number of merkle roots recorded since the beginning
     */
    function merkleRootCount() public view override returns (uint256 count) {
        count = merkleRootSet.count();
    }

    /**
     * @notice Enumerate the recorded merkle roots.
     * @param index Row to return.
     * @return merkleRoot The root stored at the row.
     */
    function merkleRootAtIndex(uint256 index) external view override returns (bytes32 merkleRoot) {
        if (index >= merkleRootSet.count())
            revert Unacceptable({
                reason: "index"
            });
        merkleRoot = merkleRootSet.keyAtIndex(index);
    }

    /**
     * @notice Check for existence in history.
     * @param merkleRoot The root to check.
     * @return isIndeed True if the root has been recorded.
     */
    function isMerkleRoot(bytes32 merkleRoot) external view override returns (bool isIndeed) {
        isIndeed = merkleRootSet.exists(merkleRoot);
    }

    /**
     @notice Return the lastest merkle root recorded. 
     @return root The latest merkle root recorded.
     */
    function latestRoot() external view override returns (bytes32 root) {
        if (merkleRootSet.count() > 0) {
            root= merkleRootSet.keyAtIndex(merkleRootSet.count() - 1);
        }
    }
}

