     * @dev The minimum policy disablement period is the minimum time that must pass before a policy can be disabled.
     */
    function minimumPolicyDisablementPeriod() external view override returns (uint256 period) {
        period = policyStorage.minimumPolicyDisablementPeriod;
    }
}

contracts/keyring/ruleRegistry/RuleRegis.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "../access/KeyringAccessControl.sol";
import "../interfaces/IRuleRegistry.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";

/**
 * @notice The RuleRegistry holds the global list of all existing Policy rules, which
 can be applied in the PolicyManager contract via the createPolicy and updatePolicy
 functions. Base Rules are managed by the Rule Admin role. Anyone can create an
 expression using an operator and existing Rules as operands.

 Rule toxicity indicates that a rule is deemed to be too precise to use safely on its own
 because doing so would possibly compromise user privacy. Toxicity is inherited by 
 expressions that consume toxic rules. It is not possible to trade where a policy enforces
 toxic rule. 

 An expression that consumes a toxic rule can be declared non-toxic after human review. This
 privilege is reserved for Keyring governance. An expression that is toxic due to inheritance
 can become non-toxic when the expression generalizes the criteria in a way that reduces
 the risks to user privacy, usually by being more inclusive of more qualifying users. 
 */

contract RuleRegistry is IRuleRegistry, KeyringAccessControl, Initializable {
    using Bytes32Set for Bytes32Set.Set;

    bytes32 private _universeRule;
    bytes32 private _emptyRule;

    bytes32 public constant override ROLE_RULE_ADMIN = keccak256("role rule admin");

    Bytes32Set.Set private ruleSet;
    mapping(bytes32 => Rule) private rules;

    /**
     * @param trustedForwarder Contract address that is allowed to relay message signers.
     */
    constructor(address trustedForwarder) KeyringAccessControl(trustedForwarder) {
        emit RuleRegistryDeployed(_msgSender(), trustedForwarder);
    }

    /**
     * @notice This upgradeable contract must be initialized.
     * @dev Initialiser function MUST be called directly after deployment
     because anyone can call it but overall only once.
     * @param universeDescription Description of the universal set Rule.
     * @param universeUri The universal set URI.
     * @param emptyDescription Description of the empty Rule.
     * @param emptyUri The empty Rule URI.
     */
    function init(
        string calldata universeDescription,
        string calldata universeUri,
        string calldata emptyDescription,
        string calldata emptyUri
    ) external override initializer {
        bytes32[] memory emptyOperands;
        if (bytes(universeDescription).length == 0)
            revert Unacceptable({
                reason: "universeDescription cannot be empty"
            });
        if (bytes(universeUri).length == 0)
            revert Unacceptable({
                reason: "universeUri cannot be empty"
            });
        if (bytes(emptyDescription).length == 0)
            revert Unacceptable({
                reason: "emptyDescription cannot be empty"
            });
        if (bytes(emptyUri).length == 0)
            revert Unacceptable({
                reason: "emptyUri cannot be empty"
            });
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(ROLE_RULE_ADMIN, _msgSender());
        _universeRule = createRule(universeDescription, universeUri, Operator.Base, emptyOperands);
        _emptyRule = createRule(emptyDescription, emptyUri, Operator.Base, emptyOperands);
        emit RuleRegistryInitialized(
            _msgSender(),
            universeDescription,
            universeUri,
            emptyDescription,
            emptyUri,
            _universeRule,
            _emptyRule
        );
    }

    /**
     * @notice Anyone can create expressions. Only the Rule Admin can create Base Rules.
     * @dev Interpretation of Expressions is deterministic.
     * @param description The description for a Base Rule. Empty for expressions.
     * @param uri Detailed information Uri for a Base Rule. Empty for expressions.
     * @param operator The expression operator (1-3, or Base (0)
     * @param operands The list of the ruleIds in the expression. Empty for Base Rules.
     * @return ruleId The unique identifier of the new Rule. Each Policy has exactly one Rule.
     */
    function createRule(
        string memory description,
        string memory uri,
        Operator operator,
        bytes32[] memory operands
    ) public override returns (bytes32 ruleId) {
        if (operator == Operator.Base)
            _checkRole(
                ROLE_RULE_ADMIN,
                _msgSender(),
                "RuleRegistry:createRule: only the RuleAdmin role can create or edit base sets"
            );
        validateRule(description, uri, operator, operands.length);
        ruleId = generateRuleId(description, operator, operands);
        // insert header information
        ruleSet.insert(ruleId, "RuleRegistry:createRule: generated duplicated id.");
        Rule storage r = rules[ruleId];
        r.description = description;
        r.operator = operator;
        r.uri = uri;
        // insert operands
        bytes32 lastOperand;
        bool toxic = false;
        for (uint256 i = 0; i < operands.length; i++) {
            if (operands[i] <= lastOperand)
                revert Unacceptable({
                    reason: "operands must be declared in ascending ruleId order"
                });
            if (!isRule(operands[i]))
                revert Unacceptable({
                    reason: "operand not found"
                });
            lastOperand = operands[i];
            if(!toxic) if(ruleIsToxic(operands[i])) toxic = true;
            r.operandSet.insert(operands[i], "RuleRegistry:createRule: 500 duplicate operand");
        }
        if(toxic) r.toxic = true;
        emit CreateRule(_msgSender(), ruleId, description, uri, toxic, operator, operands);
    }

    /**
     * @notice The rule admin can adjust the toxic flag
     * @param ruleId The rule to update
     * @param toxic True if the rule is to be set as toxic
     */
    function setToxic(bytes32 ruleId, bool toxic) external override {
        _checkRole(
            ROLE_RULE_ADMIN,
            _msgSender(),
            "RuleRegistry:setToxic: only the RuleAdmin role can set isToxic"
        );
        if (!isRule(ruleId))
            revert Unacceptable({
                reason: "ruleId not found"
            });
        rules[ruleId].toxic = toxic;
        emit SetToxic(_msgSender(), ruleId, toxic);
    }

    /**
     * @notice Checks the Rule definition validation rules.
     * @param description The rule description
     * @param uri The rule uri
     * @param operator The rule operator
     * @param operandCount The number of operands in the rule expression
     */
    function validateRule(
        string memory description,
        string memory uri,
        Operator operator,
        uint256 operandCount
    ) private pure {

        if (operator == Operator.Base) {
            if (operandCount != 0) _validationError("base rules cannot have operands");
            if (bytes(description).length == 0)
                _validationError("base rules must have a description");
            if (bytes(uri).length == 0) _validationError("base rules must have a uri");
        } else {
            if (bytes(description).length != 0)
                _validationError("only base rules can have a description");
            if (bytes(uri).length != 0) _validationError("only base rules can have a uri");

            if (operator == Operator.Complement) {
                if (operandCount != 1) _validationError("complement must have exactly one operand");
            } else if (operator == Operator.Union) {
                if (operandCount < 2) _validationError("union must have two or more operands");
            } else if (operator == Operator.Intersection) {
                if (operandCount < 2) _validationError("intersection must have two or more operands");
            } 
        }
    }

    function _validationError(string memory reason) private pure {
        revert Unacceptable({
            reason: reason
        });
    }

    /**********************************************************
     VIEW FUNCTIONS
     **********************************************************/

    /**
     @return universeRuleId The id of the universal set (everyone) Rule.
     @return emptyRuleId The id of the empty (no one) Rule.
     */
    function genesis() external view override returns (bytes32 universeRuleId, bytes32 emptyRuleId) {
        universeRuleId = _universeRule;
        emptyRuleId = _emptyRule;
    }

    /**
     * @return count Number of existing Rules in the global list.
     */
    function ruleCount() external view override returns (uint256 count) {
        count = ruleSet.count();
    }

    /**
     * @param index Iterate rules in the global list.
     * @return ruleId The Id of a rule in the global list.
     */
    function ruleAtIndex(uint256 index) external view override returns (bytes32 ruleId) {
        if (index >= ruleSet.count())
            revert Unacceptable({
                reason: "index out of range"
            });
        ruleId = ruleSet.keyAtIndex(index);
    }

    /**
     * @param ruleId The unique identifier of a rule. Each Policy has exactly one rule.
     * @return isIndeed True value if Rule exists, otherwise False.
     */
    function isRule(bytes32 ruleId) public view override returns (bool isIndeed) {
        isIndeed = ruleSet.exists(ruleId);
    }

    /**
     * @param ruleId The unique identifier of a rule. Each Policy has exactly one rule.
     * @dev Does not check existance.
     * @return description The description for a Base Rule.
     * @return uri Base Rule uri refers to detailed information about the Rule.
     * @return operator The expression operator (0-4), or Base (0)
     * @return operandCount The number of operands. 0 for Base rules.
     */
    function rule(bytes32 ruleId)
        external
        view
        override
        returns (
            string memory description,
            string memory uri,
            Operator operator,
            uint256 operandCount
        )
    {
        Rule storage r = rules[ruleId];
        return (r.description, r.uri, r.operator, r.operandSet.count());
    }

    /**
     * @param ruleId The Rule to inspect.
     * @dev Does not check existance.     
     * @return description The Rule description.
     */
    function ruleDescription(bytes32 ruleId)
        external
        view
        override
        returns (string memory description)
    {
        description = rules[ruleId].description;
    }

    /**
     * @param ruleId The Rule to inspect.
     * @dev Does not check existance.
     * @return uri The Rule uri.
     */
    function ruleUri(bytes32 ruleId) external view override returns (string memory uri) {
        uri = rules[ruleId].uri;
    }

    /**
     * @notice Toxic rules can be used in policies without approval
     * @param ruleId The rule to inspect
     * @return isIndeed True if the rule is toxic
     */
    function ruleIsToxic(bytes32 ruleId) public view override returns (bool isIndeed) {
        isIndeed = rules[ruleId].toxic;
    }

    /**
     * @param ruleId The Rule to inspect.
     * @dev Does not check existance.     
     * @return operator The Rule operator.
     */
    function ruleOperator(bytes32 ruleId) external view override returns (Operator operator) {
        operator = rules[ruleId].operator;
    }

    /**
     * @param ruleId The Rule to inspect.
     * @dev Does not check Rule existance.
     * @return count The number of operands in the Rule expression.
     */
    function ruleOperandCount(bytes32 ruleId) external view override returns (uint256 count) {
        count = rules[ruleId].operandSet.count();
    }

    /**
     * @param ruleId The Rule to inspect.
     * @param index The operand list row to inspect.
     * @dev Does not check Rule existance.
     * @return operandId A Rule id.
     */
    function ruleOperandAtIndex(bytes32 ruleId, uint256 index)
        external
        view
        override
        returns (bytes32 operandId)
    {
        Rule storage r = rules[ruleId];
        if (index >= r.operandSet.count())
            revert Unacceptable({
                reason: "index out of range"
            });
        operandId = rules[ruleId].operandSet.keyAtIndex(index);
    }

    /**
     * @notice Generate a deterministic ruleId
     * @dev Warning: This does not validate the inputs. Operands must be sorted ascending to be valid.
     * @return ruleId The ruleId that will be generated if the configuration is valid
     */
    function generateRuleId(
        string memory description,
        Operator operator,
        bytes32[] memory operands
    ) public pure override returns (bytes32 ruleId) {
        if (operands.length == 0) {
            // Base rule IDs are derived from immutable descriptions. No duplicates.
            ruleId = keccak256(bytes(description));
        } else {
            // algebraic rule IDs are derived from operator and operands. No duplicates.
            ruleId = keccak256(abi.encodePacked(operator, operands));
        }
    }
}

contracts/keyring/tokens/KycERC20.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "../interfaces/IKycERC20.sol";
import "../integration/KeyringGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Wrapper.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 @notice This contract illustrates how an immutable KeyringGuard can be wrapped around collateral tokens 
 (e.g. DAI Token). Specify the token to wrap and the new name/symbol of the wrapped token - then good to go!
 Tokens can only be transferred to an address that maintains compliance with the configured policy.
 */

contract KycERC20 is IKycERC20, ERC20Permit, ERC20Wrapper, KeyringGuard {

    using SafeERC20 for IERC20;

    string private constant MODULE = "KycERC20";

    /**
     * @param config Keyring contract addresses. See IKycERC20. 
     * @param policyId_ The unique identifier of a Policy.
     * @param maximumConsentPeriod_ The upper limit for user consent deadlines. 
     * @param name_ The name of the new wrapped token. Passed to ERC20.constructor to set the ERC20.name
     * @param symbol_ The symbol for the new wrapped token. Passed to ERC20.constructor to set the ERC20.symbol
     */
    constructor(
        KeyringConfig memory config,
        uint32 policyId_,
        uint32 maximumConsentPeriod_,
        string memory name_,
        string memory symbol_
    )
        ERC20(name_, symbol_)
        ERC20Permit(name_)
        ERC20Wrapper(IERC20(config.collateralToken))
        KeyringGuard(config, policyId_, maximumConsentPeriod_)
    {
        if (config.collateralToken == NULL_ADDRESS)
            revert Unacceptable({
                reason: "collateral token cannot be empty"
            });
        if (bytes(name_).length == 0)
            revert Unacceptable({
                reason: "name_ cannot be empty"
            });
        if (bytes(symbol_).length == 0)
            revert Unacceptable({
                reason: "symbol_ cannot be empty"
            });
    }

    /**
     * @notice Returns decimals based on the underlying token decimals
     * @return uint8 decimals integer
     */
    function decimals() public view override(ERC20, ERC20Wrapper) returns (uint8) {
        return ERC20Wrapper.decimals();
    }

    /**
     * @notice Deposit underlying tokens and mint the same number of wrapped tokens.
     * @param trader Recipient of the wrapped tokens
     * @param amount Quantity of underlying tokens from _msgSender() to exchange for wrapped tokens (to account) at 1:1
     */
    function depositFor(address trader, uint256 amount)
        public
        override(IKycERC20, ERC20Wrapper)
        returns (bool)
    {
        if(trader != _msgSender()) {
            if (!isAuthorized(_msgSender(), trader)) 
                revert Unacceptable({
                    reason: "trader not authorized"
                });
            }
        return ERC20Wrapper.depositFor(trader, amount);
    }

    /**
     * @notice Burn a number of wrapped tokens and withdraw the same number of underlying tokens.
     * @param trader Recipient of the underlying tokens
     * @param amount Quantity of wrapped tokens from _msgSender() to exchange for underlying tokens (to account) at 1:1
     */
    function withdrawTo(address trader, uint256 amount)
        public
        override(IKycERC20, ERC20Wrapper)
        returns (bool)
    {
        if(trader != _msgSender()) {
            if (!isAuthorized(_msgSender(), trader)) 
                revert Unacceptable({
                    reason: "trader not authorized"
                });
            }
        return ERC20Wrapper.withdrawTo(trader, amount);
    }

    /**
     * @notice Wraps the inherited ERC20.transfer function with the keyringCompliance guard.
     * @param to The recipient of amount
     * @param amount The amount to transfer.
     * @return bool True if successfully executed.
     */
    function transfer(address to, uint256 amount)
        public
        override(IERC20, ERC20)
        checkKeyring(_msgSender(), to)
        returns (bool)
    {
        return ERC20.transfer(to, amount);
    }

    /**
     * @notice Wraps the inherited ERC20.transferFrom function with the keyringCompliance guard.
     * @param from The sender of amount
     * @param to The recipient of amount
     * @param amount The amount to be deducted from the to's allowance.
     * @return bool True if successfully executed.
     */
    function transferFrom(address from, address to, uint256 amount) 
        public 
        override(IERC20, ERC20)
        checkKeyring(from, to)
        returns (bool)
    {
        return ERC20.transferFrom(from, to, amount);
    }

    /**
     * @notice Returns ERC2771 signer if msg.sender is a trusted forwarder, otherwise returns msg.sender.
     * @return sender User deemed to have signed the transaction.
     */
    function _msgSender()
        internal
        view
        virtual
        override(KeyringAccessControl, Context)
        returns (address sender)
    {
        return ERC2771Context._msgSender();
    }

    /**
     * @notice Returns msg.data if not from a trusted forwarder, or truncated msg.data if the signer was 
     * appended to msg.data
     * @dev Although not currently used, this function forms part of ERC2771 so is included for completeness.
     * @return data Data deemed to be the msg.data
     */
    function _msgData()
        internal
        view
        virtual
        override(KeyringAccessControl, Context)
        returns (bytes calldata)
    {
        return ERC2771Context._msgData();
    }

}

contracts/keyring/userPolicies/UserPolic.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "../lib/AddressSet.sol";
import "../access/KeyringAccessControl.sol";
import "../interfaces/IUserPolicies.sol";
import "../interfaces/IPolicyManager.sol";

/**
 @notice Users select one policy. Attestors are required to confirm compatibility of the user policy with
 the admission policy to check before issuing attestations. Traders may also define approves which are
 counterparties they will trade with even if compliance cannot be confirmed by an attestor. Approves
 only apply where admission policy owners have set the admission policy allowUserApproves flag to true. 
 */

contract UserPolicies is IUserPolicies, KeyringAccessControl {

    using AddressSet for AddressSet.Set;
    address private constant NULL_ADDRESS = address(0);

    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    address public immutable policyManager;

    mapping(address => uint32) public override userPolicies;
    mapping(address => AddressSet.Set) private approvedCounterparties;

    /**
     * @param trustedForwarder Contract address that is allowed to relay message signers.
     */
    constructor(address trustedForwarder, address policyManager_) KeyringAccessControl(trustedForwarder)
    {
        if (policyManager_ == NULL_ADDRESS)
            revert Unacceptable({
                reason: "policyManager cannot be empty"
            });
        policyManager = policyManager_;
        emit Deployed(trustedForwarder, policyManager_);
    }

    /**
     @notice Users, normally auth wallets, set a policy to be checked by attestors.
     @param policyId The policy id to enable for the auth wallet. 
     */
    function setUserPolicy(uint32 policyId) external override {
        if (!IPolicyManager(policyManager).isPolicy(policyId)) 
            revert Unacceptable({
                reason: "policyId not found"
            });
        userPolicies[_msgSender()] = policyId;
        emit SetUserPolicy(_msgSender(), policyId);
    }

    /**
     @notice Trader wallets may appoint approved addresses to trade with without the protection
     of Keyring compliance checks. 
     @param approved A counterparty address to trade with unconditionally. Must not be approved. 
     */
    function addApprovedCounterparty(address approved) public override {
        if (approved == _msgSender())
            revert Unacceptable({ reason: "self approving is not permitted" });
        AddressSet.Set storage wl = approvedCounterparties[_msgSender()];
        wl.insert(approved, "UserPolicies:addTraderApproved");
        emit AddApprovedCounterparty(_msgSender(), approved);
    }

    /**
     @notice Trader wallets may appoint approved addresses to trade with without the protection
     of Keyring compliance checks. 
     @param approved Counterparty addresses to trade with unconditionally. Must not be approved. 
     */
    function addApprovedCounterparties(address[] calldata approved) external override {
        for (uint256 i=0; i<approved.length; i++) {
            addApprovedCounterparty(approved[i]);
        }
    }    

    /**
     @notice Trader wallets may appoint approved addresses to trade with without the protection
     of Keyring compliance checks.
     @param approved A counterparty to re-enable compliance checks. Must be approved. 
     */
    function removeApprovedCounterparty(address approved) public override {
        AddressSet.Set storage wl = approvedCounterparties[_msgSender()];
        wl.remove(approved, "UserPolicies:addTraderApproved");
        emit RemoveApprovedCounterparty(_msgSender(), approved);
    }

    /**
     @notice Trader wallets may appoint approved addresses to trade with without the protection
     of Keyring compliance checks. 
     @param approved Counterparty addresseses to re-enable compliance checks. Must be approved.
     */
    function removeApprovedCounterparties(address[] calldata approved) external override {
        for (uint256 i=0; i<approved.length; i++) {
            removeApprovedCounterparty(approved[i]);
        }
    }    

    /**
     * @notice Count the addresses on a trader approve.
     * @param trader The trader approve to inspect.
     * @return count The number of addresses on a trader approve.
     */
    function approvedCounterpartyCount(address trader) external view override returns (uint256 count) {
        count = approvedCounterparties[trader].count();
    }

    /**
     * @notice Iterate the addresses on a trader approve. 
     * @param trader The trader approve to inspect. 
     * @param index The row to inspect. 
     * @return approved The address in the trader approve at the index row. 
     */
    function approvedCounterpartyAtIndex(
        address trader, 
        uint256 index
    ) external view override returns (address approved) {
        approved = approvedCounterparties[trader].keyAtIndex(index);
    }

    /**
     * @notice check if a counterparty is approved by a trader.
     * @param trader The trader approve to inspect.
     * @param counterparty The address to search for on the trader approve. 
     * @return isIndeed True if the counterparty is present on the trader approve. 
     */
    function isApproved(address trader, address counterparty) external view override returns (bool isIndeed) {
        isIndeed = approvedCounterparties[trader].exists(counterparty);
    }
}

contracts/keyring/walletCheck/WalletChec.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "../interfaces/IWalletCheck.sol";
import "../degradable/Degradable.sol";

/**
 * @notice Wallet checks are on-chain whitelists that can contain information gathered by
 * off-chain processes. Policies can specify which wallet checks must be checked on a just-in-time
 * basis. This contract establishes the interface that all wallet check contracts must implement. 
 * Future wallet check instances may employ additional logic. There is a distinct instance of a 
 * wallet check for each on-chain check. 
 */

contract WalletCheck is IWalletCheck, Degradable {

    address private constant NULL_ADDRESS = address(0);
    bytes32 public constant override ROLE_WALLETCHECK_LIST_ADMIN = keccak256("wallet check list admin role");
    bytes32 public constant override ROLE_WALLETCHECK_META_ADMIN = keccak256("wallet check meta admin");
    string public uri;

    // mapping(address => uint256) public override birthday;

    /**
    * @dev Modifier to restrict access to functions to wallet check list admins only. 
    */
    modifier onlyWalletCheckListAdmin() {
        _checkRole(
            ROLE_WALLETCHECK_LIST_ADMIN, 
            _msgSender(), 
            "WalletCheck::onlyWalletCheckListAdmin");
        _;
    }

    /**
    * @dev Modifier to restrict access to functions to wallet check meta admins only. 
    */
    modifier onlyWalletCheckMetaAdmin() {
        _checkRole(
            ROLE_WALLETCHECK_META_ADMIN, 
            _msgSender(), 
            "WalletCheck::onlyWalletCheckMetaAdmin");
        _;
    }

    /**
     * @param trustedForwarder_ Contract address that is allowed to relay message signers.
     * @param policyManager_ The policy manager contract address.
     * @param maximumConsentPeriod_ The maximum allowable user consent period.
     * @param uri_ The uri of the wallet check list. 
     */
    constructor(
        address trustedForwarder_,
        address policyManager_,
        uint256 maximumConsentPeriod_, 
        string memory uri_
    ) 
        Degradable(
            trustedForwarder_,
            policyManager_,
            maximumConsentPeriod_
        ) 
    {
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(ROLE_WALLETCHECK_META_ADMIN, _msgSender());
        emit Deployed(_msgSender(), trustedForwarder_, policyManager_, maximumConsentPeriod_, uri);
        updateUri(uri_);
    }

    /**
     * @notice The wallet check admin can set the uri of the list maintained in this contract. 
     * @param uri_ The new uri.
     */
    function updateUri(string memory uri_) public override onlyWalletCheckMetaAdmin {
        if (bytes(uri_).length == 0) 
            revert Unacceptable({
                reason: "uri_ cannot be empty"
            });
        uri = uri_;
        emit UpdateUri(_msgSender(), uri_);
    }

    /**
     * @notice Record a wallet check. 
     * @param wallet The subject wallet.
     * @param whitelisted True if the wallet has passed the checks represented by this contract.
     * @param timestamp The effective time of the wallet check. Not used if whitelisted is false.
     */
    function setWalletCheck(
        address wallet, 
        bool whitelisted, 
        uint256 timestamp
    ) external override onlyWalletCheckListAdmin {
        if (whitelisted) {
            _recordUpdate(wallet, timestamp);
        } else {
            subjectUpdates[bytes32(uint256(uint160(wallet)))] = 0;
        }
    }

    /**
     * @notice Inspect the Wallet Check.
     * @param observer The observer for degradation mitigation consent. 
     * @param wallet The wallet to inspect. 
     * @param admissionPolicyId The admission policy for the wallet to inspect.
     * @return passed True if a wallet check exists or if mitigation measures are applicable.
     */
    function checkWallet(
        address observer, 
        address wallet,
        uint32 admissionPolicyId
    ) external override returns (bool passed) {

        passed = _checkKey(
            observer,
            wallet,
            admissionPolicyId
        );
    }    

}

