contracts/portalgate/InstanceRegistry.so.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.14;

import "hardhat/console.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/ITornadoInstance.sol";
import "./PGRouter.sol";

contract InstanceRegistry {
    using SafeERC20 for IERC20;

    enum InstanceState {
        DISABLED,
        ENABLED, // is enabled but mining is disabled
        MINABLE // is enabled and mining is enabled
    }

    struct Instance {
        bool isERC20;
        IERC20 token;
        InstanceState state;
        // the fee of the uniswap pool which will be used to get a TWAP
        uint24 uniswapPoolSwappingFee;
        // the fee the protocol takes from relayer, it should be multiplied by PROTOCOL_FEE_DIVIDER from FeeManager.sol
        uint32 protocolFeePercentage;
        uint256 maxDepositAmount;
    }

    struct TornadoConfig {
        ITornadoInstance addr;
        Instance instance;
    }

    address public immutable governance;
    PGRouter public router;

    mapping(ITornadoInstance => Instance) public instances;
    ITornadoInstance[] public instanceIds;

    event InstanceStateUpdated(
        ITornadoInstance indexed instance,
        InstanceState state
    );
    event RouterRegistered(address pgRouter);

    modifier onlyGovernance() {
        require(msg.sender == governance, "Not authorized");
        _;
    }

    constructor(address _governance) {
        governance = _governance;
    }

    /**
     * @dev initialise a set of tornado instances.
     */
    function initInstances(TornadoConfig[] memory _instances) external onlyGovernance {
        for (uint256 i = 0; i < _instances.length; i++) {
            _updateInstance(_instances[i]);
            instanceIds.push(_instances[i].addr);
        }
    }

    /**
     * @dev Add or update an instance.
     */
    function updateInstanceState(ITornadoInstance _addr, InstanceState _state) external virtual onlyGovernance {
        Instance storage _instance = instances[_addr];
        _instance.state = _state;
        emit InstanceStateUpdated(_addr, _state);
    }

    /**
     * @dev Add or update an instance.
     */
    function updateInstance(
        TornadoConfig calldata _tornadoConf
    ) external virtual onlyGovernance {
        require(
            _tornadoConf.instance.state != InstanceState.DISABLED,
            "Use removeInstance() for remove"
        );
        if (instances[_tornadoConf.addr].state == InstanceState.DISABLED) {
            instanceIds.push(_tornadoConf.addr);
        }
        _updateInstance(_tornadoConf);
    }

    /**
     * @dev Remove an instance.
     * @param _instanceId The instance id in `instanceIds` mapping to remove.
     */
    function removeInstance(
        uint256 _instanceId
    ) external virtual onlyGovernance {
        ITornadoInstance _instance = instanceIds[_instanceId];
        (bool isERC20, IERC20 token) = (
            instances[_instance].isERC20,
            instances[_instance].token
        );

        if (isERC20) {
            uint256 allowance = token.allowance(
                address(router),
                address(_instance)
            );
            if (allowance != 0) {
                router.approveExactToken(token, address(_instance), 0);
            }
        }

        delete instances[_instance];
        instanceIds[_instanceId] = instanceIds[instanceIds.length - 1];
        instanceIds.pop();
        emit InstanceStateUpdated(_instance, InstanceState.DISABLED);
    }

    /**
     * @notice This function should allow governance to set a new protocol fee for relayers
     * @param instance the to update
     * @param newFee the new fee to use
     * */
    function setProtocolFee(
        ITornadoInstance instance,
        uint32 newFee
    ) external onlyGovernance {
        instances[instance].protocolFeePercentage = newFee;
    }

    /**
     * @notice This function should allow governance to set a new tornado proxy address
     * @param routerAddress address of the new proxy
     * */
    function setPGRouter(address routerAddress) external onlyGovernance {
        router = PGRouter(routerAddress);
        emit RouterRegistered(routerAddress);
    }

    function _updateInstance(TornadoConfig memory _tornadoConf) internal virtual {
        instances[_tornadoConf.addr] = _tornadoConf.instance;

        if (_tornadoConf.instance.isERC20) {
            IERC20 token = IERC20(_tornadoConf.addr.token());
            require(token == _tornadoConf.instance.token, "Incorrect token");
            uint256 allowance = token.allowance(
                address(router),
                address(_tornadoConf.addr)
            );

            if (allowance == 0) {
                router.approveExactToken(
                    token,
                    address(_tornadoConf.addr),
                    type(uint256).max
                );
            }
        }
        emit InstanceStateUpdated(_tornadoConf.addr, _tornadoConf.instance.state);
    }

    /**
     * @dev Returns all instance configs
     */
    function getAllInstances() public view returns (TornadoConfig[] memory result) {
        result = new TornadoConfig[](instanceIds.length);
        for (uint256 i = 0; i < instanceIds.length; i++) {
            ITornadoInstance _instance = instanceIds[i];
            result[i] = TornadoConfig({
                addr: _instance,
                instance: instances[_instance]
            });
        }
    }

    /**
     * @dev Returns all instance addresses
     */
    function getAllInstanceAddresses()
        public
        view
        returns (ITornadoInstance[] memory result)
    {
        result = new ITornadoInstance[](instanceIds.length);
        for (uint256 i = 0; i < instanceIds.length; i++) {
            result[i] = instanceIds[i];
        }
    }

    /// @notice get erc20 tornado instance token
    /// @param instance the interface (contract) key to the instance data
    function getPoolToken(
        ITornadoInstance instance
    ) external view returns (address) {
        return address(instances[instance].token);
    }
}

contracts/portalgate/KycERC20.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.14;

import "../keyring/interfaces/IKycERC20.sol";
import "../keyring/integration/KeyringGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Wrapper.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 @notice This contract illustrates how an immutable KeyringGuard can be wrapped around collateral tokens
 (e.g. DAI Token). Specify the token to wrap and the new name/symbol of the wrapped token - then good to go!
 Tokens can only be transferred to an address that maintains compliance with the configured policy.
 */

contract KycERC20 is IKycERC20, ERC20Permit, ERC20Wrapper, KeyringGuard {

    using SafeERC20 for IERC20;

    string private constant MODULE = "KycERC20";

    /**
     * @param config Keyring contract addresses. See IKycERC20.
     * @param policyId_ The unique identifier of a Policy.
     * @param maximumConsentPeriod_ The upper limit for user consent deadlines.
     * @param name_ The name of the new wrapped token. Passed to ERC20.constructor to set the ERC20.name
     * @param symbol_ The symbol for the new wrapped token. Passed to ERC20.constructor to set the ERC20.symbol
     */
    constructor(
        KeyringConfig memory config,
        uint32 policyId_,
        uint32 maximumConsentPeriod_,
        string memory name_,
        string memory symbol_
    )
        ERC20(name_, symbol_)
        ERC20Permit(name_)
        ERC20Wrapper(IERC20(config.collateralToken))
        KeyringGuard(config, policyId_, maximumConsentPeriod_)
    {
        if (config.collateralToken == NULL_ADDRESS)
            revert Unacceptable({
                reason: "collateral token cannot be empty"
            });
        if (bytes(name_).length == 0)
            revert Unacceptable({
                reason: "name_ cannot be empty"
            });
        if (bytes(symbol_).length == 0)
            revert Unacceptable({
                reason: "symbol_ cannot be empty"
            });
    }

    /**
     * @notice Returns decimals based on the underlying token decimals
     * @return uint8 decimals integer
     */
    function decimals() public view override(ERC20, ERC20Wrapper) returns (uint8) {
        return ERC20Wrapper.decimals();
    }

    /**
     * @notice Deposit underlying tokens and mint the same number of wrapped tokens.
     * @param trader Recipient of the wrapped tokens
     * @param amount Quantity of underlying tokens from _msgSender() to exchange for wrapped tokens (to account) at 1:1
     */
    function depositFor(address trader, uint256 amount)
        public
        override(IKycERC20, ERC20Wrapper)
        returns (bool)
    {
        if(trader != _msgSender()) {
            if (!isAuthorized(_msgSender(), trader))
                revert Unacceptable({
                    reason: "trader not authorized"
                });
            }
        return ERC20Wrapper.depositFor(trader, amount);
    }

    /**
     * @notice Burn a number of wrapped tokens and withdraw the same number of underlying tokens.
     * @param trader Recipient of the underlying tokens
     * @param amount Quantity of wrapped tokens from _msgSender() to exchange for underlying tokens (to account) at 1:1
     */
    function withdrawTo(address trader, uint256 amount)
        public
        override(IKycERC20, ERC20Wrapper)
        returns (bool)
    {
        if(trader != _msgSender()) {
            if (!isAuthorized(_msgSender(), trader))
                revert Unacceptable({
                    reason: "trader not authorized"
                });
            }
        return ERC20Wrapper.withdrawTo(trader, amount);
    }

    /**
     * @notice Wraps the inherited ERC20.transfer function with the keyringCompliance guard.
     * @param to The recipient of amount
     * @param amount The amount to transfer.
     * @return bool True if successfully executed.
     */
    function transfer(address to, uint256 amount)
        public
        override(IERC20, ERC20)
        checkKeyring(_msgSender(), to)
        returns (bool)
    {
        return ERC20.transfer(to, amount);
    }

    /**
     * @notice Wraps the inherited ERC20.transferFrom function with the keyringCompliance guard.
     * @param from The sender of amount
     * @param to The recipient of amount
     * @param amount The amount to be deducted from the to's allowance.
     * @return bool True if successfully executed.
     */
    function transferFrom(address from, address to, uint256 amount)
        public
        override(IERC20, ERC20)
        checkKeyring(from, to)
        returns (bool)
    {
        return ERC20.transferFrom(from, to, amount);
    }

    /**
     * @notice Returns ERC2771 signer if msg.sender is a trusted forwarder, otherwise returns msg.sender.
     * @return sender User deemed to have signed the transaction.
     */
    function _msgSender()
        internal
        view
        virtual
        override(KeyringAccessControl, Context)
        returns (address sender)
    {
        return ERC2771Context._msgSender();
    }

    /**
     * @notice Returns msg.data if not from a trusted forwarder, or truncated msg.data if the signer was
     * appended to msg.data
     * @dev Although not currently used, this function forms part of ERC2771 so is included for completeness.
     * @return data Data deemed to be the msg.data
     */
    function _msgData()
        internal
        view
        virtual
        override(KeyringAccessControl, Context)
        returns (bytes calldata)
    {
        return ERC2771Context._msgData();
    }

}

contracts/portalgate/KycETH.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "../keyring/integration/KeyringGuard.sol";


contract KycETH is KeyringGuard {
    string public name     = "KYC Ether";
    string public symbol   = "kycETH";
    uint8  public decimals = 18;

    event  Approval(address indexed owner, address indexed spender, uint amount);
    event  Transfer(address indexed from, address indexed to, uint amount);
    event  Deposit(address indexed to, uint amount);
    event  Withdrawal(address indexed from, uint amount);

    mapping (address => uint)                       public  balanceOf;
    mapping (address => mapping (address => uint))  public  allowance;

    /**
     * @param config Keyring contract addresses. See IKycERC20.
     * @param policyId_ The unique identifier of a Policy.
     * @param maximumConsentPeriod_ The upper limit for user consent deadlines.
     */
    constructor(
        KeyringConfig memory config,
        uint32 policyId_,
        uint32 maximumConsentPeriod_
    ) KeyringGuard(config, policyId_, maximumConsentPeriod_)
    {}

    function depositFor() public payable
    {
      balanceOf[_msgSender()] += msg.value;
      emit Deposit(_msgSender(), msg.value);
    }

    function withdrawTo(address trader, uint amount) public
    {
      if(trader != _msgSender()) {
        if (!isAuthorized(_msgSender(), trader)) revert Unacceptable({reason: "trader not authorized"});
      }

      require(balanceOf[_msgSender()] >= amount);
      balanceOf[_msgSender()] -= amount;
      payable(trader).transfer(amount);
      emit Withdrawal(_msgSender(), amount);
    }

    function totalSupply() public view returns (uint) {
        return address(this).balance;
    }

    function approve(address spender, uint amount) public returns (bool) {
        allowance[_msgSender()][spender] = amount;
        emit Approval(_msgSender(), spender, amount);
        return true;
    }

    function transfer(address to, uint amount)
        public
        checkKeyring(_msgSender(), to)
        returns (bool)
    {
        return transferFrom(_msgSender(), to, amount);
    }

    function transferFrom(address from, address to, uint amount)
        public
        checkKeyring(from, to)
        returns (bool)
    {
        require(balanceOf[from] >= amount);

        if (from != _msgSender() && allowance[from][_msgSender()] > 0) {
            require(allowance[from][_msgSender()] >= amount);
            allowance[from][_msgSender()] -= amount;
        }

        balanceOf[from] -= amount;
        balanceOf[to] += amount;

        emit Transfer(from, to, amount);

        return true;
    }

}

contracts/portalgate/PGRouter.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "hardhat/console.sol";

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./RelayerRegistry.sol";
import "./InstanceRegistry.sol";
import "../interfaces/ITornadoInstance.sol";
import "../tornado-core/Tornado.sol";
import "../interfaces/ITornadoTrees.sol";

contract PGRouter is Initializable {
  using SafeERC20 for IERC20;

  event EncryptedNote(address indexed sender, bytes encryptedNote);
  event TornadoTreesUpdated(ITornadoTrees addr);

  address public governance;
  InstanceRegistry public instanceRegistry;
  RelayerRegistry public relayerRegistry;
  ITornadoTrees public tornadoTrees;

  modifier onlyGovernance() {
    require(msg.sender == governance, "Not authorized");
    _;
  }

  modifier onlyInstanceRegistry() {
    require(msg.sender == address(instanceRegistry), "Not authorized");
    _;
  }

  constructor(address _tornadoTrees, address _governance, address _instanceRegistry, address _relayerRegistry) {
    tornadoTrees = ITornadoTrees(_tornadoTrees);
    governance = _governance;
    instanceRegistry = InstanceRegistry(_instanceRegistry);
    relayerRegistry = RelayerRegistry(_relayerRegistry);
  }

  /**
    @notice For proxy pattern
  */
  function initialize(address _tornadoTrees, address _governance, address _instanceRegistry, address _relayerRegistry) public initializer {
    tornadoTrees = ITornadoTrees(_tornadoTrees);
    governance = _governance;
    instanceRegistry = InstanceRegistry(_instanceRegistry);
    relayerRegistry = RelayerRegistry(_relayerRegistry);
  }

  /**
    @notice Deposit funds into the contract.
    @param _commitment the note commitment, which is PedersenHash(nullifier + secret)
  */
  function deposit(ITornadoInstance _tornado, bytes32 _commitment, bytes calldata _encryptedNote) public payable virtual {
    (
      bool isERC20,
      IERC20 token,
      InstanceRegistry.InstanceState state,
      ,
      ,
      uint256 maxDepositAmount
    ) = instanceRegistry.instances(_tornado);
    require(state != InstanceRegistry.InstanceState.DISABLED, "The instance is not supported");
    require(token.balanceOf(address(_tornado)) < maxDepositAmount, "Exceed deposit Cap for the pool");

    if (isERC20) {
      token.safeTransferFrom(msg.sender, address(this), _tornado.denomination());
    }
    _tornado.deposit{ value: msg.value }(_commitment);

    if (state == InstanceRegistry.InstanceState.MINABLE) {
      tornadoTrees.registerDeposit(address(_tornado), _commitment);
    }

    emit EncryptedNote(msg.sender, _encryptedNote);
  }

  /**
    @notice Withdraw a deposit from the contract. Relayer withdrawn should have different _relayer and _recipient addresses.
    @param _tornado TC pool instance address
    @param _proof is a zkSNARK proof data, and input is an array of circuit public inputs `input` array
    @param _root merkle root of all deposits in the contract
    @param _nullifierHash hash of unique deposit nullifier to prevent double spends
    @param _recipient the recipient address to recieve the token
    @param _relayer the relayer address
    @param _fee the token amount sent to relayer as fee
    @param _refund the eth amount sent to recipient as gas
  */
  function withdraw(
    ITornadoInstance _tornado,
    bytes calldata _proof,
    bytes32 _root,
    bytes32 _nullifierHash,
    address payable _recipient,
    address payable _relayer,
    uint256 _fee,
    uint256 _refund
  ) public payable virtual {
    (
      ,
      ,
      InstanceRegistry.InstanceState state,
      ,
      ,
    ) = instanceRegistry.instances(_tornado);
    require(state != InstanceRegistry.InstanceState.DISABLED, "The instance is not supported");

    if (_relayer != _recipient) {
      require(
        relayerRegistry.isRelayerRegistered(_relayer) && relayerRegistry.isRelayerRegistered(msg.sender) && msg.sender == _relayer,
        "Invalid Relayer."
      );
    }

    // keyring attestation needed. TBC


    _tornado.withdraw{value:msg.value}(_proof, _root, _nullifierHash, _recipient, _relayer, _fee, _refund);

    if (state == InstanceRegistry.InstanceState.MINABLE) {
      tornadoTrees.registerWithdrawal(address(_tornado), _nullifierHash);
    }
  }

  /**
   * @dev Sets `amount` allowance of `_spender` over the router's (this contract) tokens.
   */
  function approveExactToken(IERC20 _token, address _spender, uint256 _amount) external onlyInstanceRegistry {
    _token.safeApprove(_spender, _amount);
  }

  /**
   * @notice Manually backup encrypted notes
   */
  function backupNotes(bytes[] calldata _encryptedNotes) external virtual {
    for (uint256 i = 0; i < _encryptedNotes.length; i++) {
      emit EncryptedNote(msg.sender, _encryptedNotes[i]);
    }
  }

  function setTornadoTreesContract(ITornadoTrees _tornadoTrees) external {
    tornadoTrees = _tornadoTrees;
    emit TornadoTreesUpdated(_tornadoTrees);
  }

  /// @dev Method to claim junk and accidentally sent tokens
  function rescueTokens(IERC20 _token, address payable _to, uint256 _amount) external virtual onlyGovernance {
    require(_to != address(0), "PG: can not send to zero address");

    if (address(_token) == address(0)) {
      // for Ether
      uint256 totalBalance = address(this).balance;
      uint256 balance = Math.min(totalBalance, _amount);
      _to.transfer(balance);
    } else {
      // any other erc20
      uint256 totalBalance = _token.balanceOf(address(this));
      uint256 balance = Math.min(totalBalance, _amount);
      require(balance > 0, "PG: trying to send 0 balance");
      _token.safeTransfer(_to, balance);
    }
  }
}

